import { useState } from 'react'

import Layout from '../components/Layout'
import Characters from '../Characters.json'
import CharCard from '../components/CharCard'


const index = (props) => {
    console.log(props)
    const [character, setCharacters] = useState(Characters)
    console.log(character)

    const charTEST = Characters.map(char => (
        <div
            key={char.id}
            className='card'
        >
        <span>
        <h1> {char.name} </h1>
        <img src={char.image}/>
        </span>
        </div>
    ))

    return (
        <>
        <Layout>
            {/* ?- make this('<h1>') into a js-jsxVar >> 'switch' -? */}
        <h1>
           Welcome to ClickMeGuy!
        </h1>
        <div className="container Holder" style={{ 
            textAlign:'center',
            backgroundColor:'lightgrey',
            borderRadius:10.1618,
            // margin:'0 auto',
            // maxWidth:945 
        }}>
            <h1>this is a TEST_H1_TAG</h1>
            <div className="row">
                {/* CHARACTERS */}
                {/* {charTEST} */}
                {character.map(char => (
                    <CharCard 
                        id={char.id}
                        name={char.name}
                        image={char.image}
                    />
                ))}
                
            </div>
            
            
        </div>
        </Layout>
        </>
    );
};

// index.getInitialProps = async function

export default index
